Property.Value:
BEGIN SET NOCOUNT ON;
DECLARE @MaxTimeStamp DATETIME; SELECT @MaxTimeStamp = MAX(CreatedAt) FROM dbo.OptionData;
;WITH recent AS ( SELECT * FROM dbo.OptionData WHERE CreatedAt >= DATEADD(MINUTE, -ABS(@LookbackMinutes), @MaxTimeStamp) ), ranked AS ( SELECT *, ROW_NUMBER() OVER (PARTITION BY StrikePrice, OptionType ORDER BY CreatedAt DESC, Id DESC) AS rn FROM recent ), latest AS ( SELECT * FROM ranked WHERE rn = 1 ), previous AS ( SELECT * FROM ranked WHERE rn = 2 ), joined AS ( SELECT L.Id AS OptionDataId, L.StrikePrice, L.OptionType, L.LastPrice, P.LastPrice AS PrevLastPrice, L.OI, P.OI AS PrevOI, L.Volume, P.Volume AS PrevVolume, L.ImpliedVolatility, P.ImpliedVolatility AS PrevImpliedVolatility, L.Delta, L.Gamma, L.Vega, L.CreatedAt, CASE WHEN P.LastPrice IS NULL OR P.LastPrice = 0 THEN 0 ELSE (L.LastPrice - P.LastPrice) * 100.0 / P.LastPrice END AS PricePct, CASE WHEN P.OI IS NULL OR P.OI = 0 THEN 0 ELSE (L.OI - P.OI) * 100.0 / P.OI END AS OIPct, CASE WHEN P.Volume IS NULL OR P.Volume = 0 THEN 0 ELSE (L.Volume - P.Volume) * 100.0 / P.Volume END AS VolumePct, CASE WHEN P.ImpliedVolatility IS NULL OR P.ImpliedVolatility = 0 THEN 0 ELSE (L.ImpliedVolatility - P.ImpliedVolatility) * 100.0 / P.ImpliedVolatility END AS IVPct FROM latest L LEFT JOIN previous P ON L.StrikePrice = P.StrikePrice AND L.OptionType = P.OptionType ), sr AS ( SELECT (SELECT TOP 1 StrikePrice FROM latest WHERE OptionType = 'ce' ORDER BY OI DESC) AS ResistanceStrike, (SELECT TOP 1 StrikePrice FROM latest WHERE OptionType = 'pe' ORDER BY OI DESC) AS SupportStrike )
INSERT INTO dbo.ScalpSignal ( OptionDataId, StrikePrice, OptionType, SignalType, SignalScore, LastPrice, PrevLastPrice, PricePctChange, OI, PrevOI, OIPctChange, Volume, PrevVolume, VolumePctChange, ImpliedVolatility, PrevImpliedVolatility, IVPctChange, Delta, Gamma, Vega, CreatedAt ) SELECT j.OptionDataId, j.StrikePrice, j.OptionType, CASE WHEN j.OptionType = 'ce' AND j.PricePct > @PricePctThreshold*2 THEN 'BUY_CE_STRONG' WHEN j.OptionType = 'pe' AND j.PricePct > @PricePctThreshold*2 THEN 'BUY_PE_STRONG' WHEN j.OptionType = 'ce' THEN 'BUY_CE' WHEN j.OptionType = 'pe' THEN 'BUY_PE' END AS SignalType, CAST( (ABS(j.PricePct)/NULLIF(@PricePctThreshold,0))*0.4 + (ABS(j.OIPct)/NULLIF(@OIPctThreshold,0))*0.4 + (ABS(j.VolumePct)/10.0)*0.2 AS DECIMAL(8,4)) AS SignalScore, j.LastPrice, j.PrevLastPrice, j.PricePct, j.OI, j.PrevOI, j.OIPct, j.Volume, j.PrevVolume, j.VolumePct, j.ImpliedVolatility, j.PrevImpliedVolatility, j.IVPct, j.Delta, j.Gamma, j.Vega, j.CreatedAt FROM joined j CROSS JOIN sr WHERE ( -- ðŸ”¹ Type 1: Breakout BUY_CE (j.OptionType = 'ce' AND j.PricePct > @PricePctThreshold AND j.OIPct > @OIPctThreshold AND j.StrikePrice >= sr.ResistanceStrike AND j.Volume >= @MinVolume AND j.Delta > 0.35 AND ((j.PricePct + j.OIPct + j.VolumePct) / 3.0) > 2.0 ) OR -- ðŸ”¹ Type 2: Breakout BUY_PE (j.OptionType = 'pe' AND j.PricePct > @PricePctThreshold AND j.OIPct > @OIPctThreshold AND j.StrikePrice <= sr.SupportStrike AND j.Volume >= @MinVolume AND ABS(j.Delta) > 0.35 AND ((j.PricePct + j.OIPct + j.VolumePct) / 3.0) > 2.0 ) OR -- ðŸ”¹ Type 3: Momentum continuation (CE or PE with high OI & IV jump) ((j.OptionType IN ('ce','pe')) AND j.PricePct > @PricePctThreshold * 1.5 AND j.OIPct > @OIPctThreshold * 1.5 AND j.IVPct > 1.0 AND j.Volume >= @MinVolume * 1.5 ) OR -- ðŸ”¹ Type 4: Reversal bounce (support-based PE or resistance-based CE) ((j.OptionType = 'pe' AND j.StrikePrice <= sr.SupportStrike AND j.PricePct > 1.0 AND j.OIPct > 0.5) OR (j.OptionType = 'ce' AND j.StrikePrice >= sr.ResistanceStrike AND j.PricePct > 1.0 AND j.OIPct > 0.5) ) ) AND NOT EXISTS (SELECT 1 FROM dbo.ScalpSignal s WHERE s.OptionDataId = j.OptionDataId); END;

CREATE PROCEDURE [dbo].[GenerateScalpSignals]  
    @PricePctThreshold DECIMAL(6,3) = 2.0,  
    @OIPctThreshold DECIMAL(6,3) = 1.0,  
    @MinVolume BIGINT = 1000,  
    @LookbackMinutes INT = 10  
AS

BEGIN 
CREATE TABLE #OptionTemp
(
    rowId BIGINT IDENTITY(1,1),
    id INT,
    FetchedAt DATETIME,
    JsonData NVARCHAR(MAX),
    Underlying VARCHAR(50)
);

INSERT INTO #OptionTemp (id, FetchedAt, JsonData, Underlying)
SELECT   Id, FetchedAt, JsonData, 'NIFTY' AS Underlying
FROM OptionChainRaw 
WHERE isProcessed IS NULL 
ORDER BY FetchedAt ASC;

DECLARE @MinId BIGINT, @MaxId BIGINT;

SELECT @MinId = MIN(RowId), @MaxId = MAX(RowId)
FROM #OptionTemp;

WHILE @MinId <= @MaxId
BEGIN
    DECLARE @Json NVARCHAR(MAX);
    DECLARE @FetchAt DATETIME;
    DECLARE @Underlying VARCHAR(50);
    DECLARE @id INT;

    SELECT 
        @Json = JsonData,
        @FetchAt = FetchedAt,
        @Underlying = Underlying,
        @id = id
    FROM #OptionTemp 
    WHERE RowId = @MinId;

    EXEC [dbo].[InsertOptionDataFromJson]
        @Json = @Json,
        @FetchAt = @FetchAt,
        @Underlying = @Underlying;

    EXEC [dbo].[GenerateScalpSignals]

    UPDATE OptionChainRaw
    SET isProcessed = 1
    WHERE Id = @id;

    SET @MinId = @MinId + 1;
END

END

BEGIN SET NOCOUNT ON;
;WITH CTE AS ( SELECT TRY_CAST(REPLACE(oc.[key], '.000000', '') AS DECIMAL(18,2)) AS StrikePrice, opt.OptionType, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.implied_volatility')) AS DECIMAL(18,8)) AS ImpliedVolatility, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.last_price')) AS DECIMAL(18,8)) AS LastPrice, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.oi')) AS BIGINT) AS OI, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.previous_close_price')) AS DECIMAL(18,8)) AS PreviousClosePrice, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.previous_oi')) AS BIGINT) AS PreviousOI, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.previous_volume')) AS BIGINT) AS PreviousVolume, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.top_ask_price')) AS DECIMAL(18,8)) AS TopAskPrice, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.top_ask_quantity')) AS BIGINT) AS TopAskQuantity, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.top_bid_price')) AS DECIMAL(18,8)) AS TopBidPrice, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.top_bid_quantity')) AS BIGINT) AS TopBidQuantity, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.volume')) AS BIGINT) AS Volume, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.greeks.delta')) AS DECIMAL(18,8)) AS Delta, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.greeks.theta')) AS DECIMAL(18,8)) AS Theta, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.greeks.gamma')) AS DECIMAL(18,8)) AS Gamma, TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.greeks.vega')) AS DECIMAL(18,8)) AS Vega, TRY_CAST(JSON_VALUE(@Json, '$.data.last_price') AS DECIMAL(18,8)) AS RootLastPrice FROM OPENJSON(@Json, '$.data.oc') AS oc CROSS APPLY (VALUES ('ce'), ('pe')) AS opt(OptionType) --WHERE TRY_CAST(JSON_VALUE(oc.value, CONCAT('$.', opt.OptionType, '.last_price')) AS DECIMAL(18,8)) > 1; ) , Filtered AS ( SELECT *, ABS(CAST(StrikePrice AS INT) - CAST(RootLastPrice AS INT)) AS DistanceFromATM FROM CTE --WHERE Volume > 1.00 )
---- Extract all strike prices INSERT INTO OptionData ( StrikePrice, OptionType, ImpliedVolatility, LastPrice, OI, PreviousClosePrice, PreviousOI, PreviousVolume, TopAskPrice, TopAskQuantity, TopBidPrice, TopBidQuantity, Volume, Delta, Theta, Gamma, Vega , UnderlyingValue, Underlying,CreatedAt ) SELECT StrikePrice, OptionType, ImpliedVolatility, LastPrice, OI, PreviousClosePrice, PreviousOI, PreviousVolume, TopAskPrice, TopAskQuantity, TopBidPrice, TopBidQuantity, Volume, Delta, Theta, Gamma, Vega, RootLastPrice, 'NIFTY',@FetchAt FROM Filtered WHERE DistanceFromATM<=50
END;

   
  
CREATE PROCEDURE [dbo].[InsertOptionDataFromJson]      
    @Json NVARCHAR(MAX),  
	@FetchAt DATETIME,
	@Underlying VARCHAR(50)
AS


BEGIN  
    SET NOCOUNT ON;  

    DECLARE   
        @Now DATETIME = GETDATE(),  
        @DayName NVARCHAR(10) = DATENAME(WEEKDAY, GETDATE()),  
        @StartTime TIME = '09:15:00',  
        @EndTime TIME = '15:30:00';   
    SET @IsOpen = 0;  

    IF @DayName IN ('Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday')  
    BEGIN   
        IF CAST(@Now AS TIME) BETWEEN @StartTime AND @EndTime  
        BEGIN  
            SET @IsOpen = 1;  
        END  
    END   
END;
